				entity.setStoredData("POINTS",Number(entity.getStoredData("POINTS"))+upPoints);//点数增加
				entity.setHealth(entity.getMaxHealth());//回满血
		    }else{
			    entity.sendMessage("§b§l[系统]§r§l你已满足升级条件，请将§d§l还没想好§r§l穿戴于盔甲栏中");
		    }//必要条件3
